/// <reference types="Cypress" />

export default (platform, device, artboardSelector, orientation) => {
  cy.get(artboardSelector).then(elem => {
    var elemPos = Cypress.dom.getElementCoordinatesByPosition(elem);
    return cy
      .get("body")
      .click(elemPos.fromViewport.left + 3, elemPos.fromViewport.top - 3);
  });

  cy.get(`.app-tutorial-right-panel > .formGroupX > .formRowX`)
    .eq[0].find(".formColX > button > div")
    .contains(device)
    .click();
  // cy.get(`.app-tutorial-right-panel > .formGroupX > .formRowX`).eq[1].find()
};
