export default selecter => {
  //cy.get(selecter).scrollIntoView();
  cy.get(selecter).then(elem => {
    var pos = Cypress.dom.getElementCoordinatesByPosition(elem).fromViewport;
    console.log(pos, "pos");
    cy.get("body")
      .click(pos.leftCenter, pos.topCenter)
      .type("{del}");
  });
};
