export function dragByStringContent(
  sourceSelecter,
  sourceContent,
  destinationSelecter,
  destinationContent
) {
  cy.get(sourceSelecter)
    .contains(sourceContent)
    .then(elem1 => {
      var sourcePos = Cypress.dom.getElementCoordinatesByPosition(elem1)
        .fromViewport;
      cy.get(destinationSelecter)
        .contains(destinationContent)
        .then(elem2 => {
          var destinationPos = Cypress.dom.getElementCoordinatesByPosition(
            elem2
          ).fromViewport;
          console.log("sourci", sourcePos);
          console.log("desti", destinationPos);
          cy.get("body")
            .trigger(
              "mousedown",
              sourcePos.leftCenter,
              sourcePos.topCenter + 10
            )
            .trigger(
              "mousemove",
              sourcePos.leftCenter,
              sourcePos.topCenter + 10
            )
            .trigger(
              "mousemove",
              destinationPos.leftCenter,
              destinationPos.topCenter + 10
            )
            .trigger(
              "mousemove",
              destinationPos.leftCenter,
              destinationPos.topCenter + 10
            )
            .trigger(
              "mouseup",
              destinationPos.leftCenter,
              destinationPos.topCenter + 10
            );
        });
    });
}
