/// <reference types="Cypress" />

export default (
  parent = ".bx-artboard-Untitled > .view-render",
  width = 100,
  height = 100,
  left = 10,
  top = 10
) => {
  cy.get(parent).then(elem => {
    var elemPos = Cypress.dom.getElementCoordinatesByPosition(elem)
      .fromViewport;
    return cy
      .get("body")
      .trigger("mousedown", elemPos.left + left, elemPos.top + top)
      .trigger("mousemove", elemPos.left + left, elemPos.top + top)
      .trigger(
        "mousemove",
        elemPos.left + left + width,
        elemPos.top + top + height
      )
      .trigger("mouseup");
  });
};
