import { getOffsetsOf } from "./sharedFuncs";

export function scrollToArtboard(selecter) {
  // cy.get(".scrollcontainer > div")
  //     .first()
  //     .scrollTo(offsets.leftCenter, offsets.top - 30);

  getOffsetsOf(selecter, ".scrollcontainer > div > div", offsets => {
    console.log(offsets);
    console.log(offsets.top - 30, "scroll");
    cy.get(".scrollcontainer > div")
      .first()
      .scrollTo(offsets.leftCenter, offsets.top - 30);
    cy.get(".scrollcontainer > div")
      .first()
      .scrollTo(offsets.left - 30, offsets.top - 30);
  });
}
