export function selectShape(selecter) {
  if (selecter) {
    cy.get(selecter).then(elem => {
      var elemPos = Cypress.dom.getElementCoordinatesByPosition(elem);
      return cy
        .get("body")
        .click(elemPos.fromViewport.leftCenter, elemPos.fromViewport.topCenter);
    });
  }
}

export function selectShapeByDblClick(selecter) {
  if (selecter) {
    cy.get(selecter).then(elem => {
      var elemPos = Cypress.dom.getElementCoordinatesByPosition(elem);
      return (
        cy
          .get("body")
          // .click(elemPos.fromViewport.leftCenter, elemPos.fromViewport.topCenter)
          .click(
            elemPos.fromViewport.leftCenter,
            elemPos.fromViewport.topCenter
          )
          .click(
            elemPos.fromViewport.leftCenter,
            elemPos.fromViewport.topCenter
          )
      );
    });
  }
}

export function selectResizerType(selecter) {
  if (selecter) {
    cy.get(selecter).then(elem => {
      var elemPos = Cypress.dom.getElementCoordinatesByPosition(elem);
      return cy
        .get("body")
        .click(
          elemPos.fromViewport.leftCenter,
          elemPos.fromViewport.topCenter + 3,
          { force: true }
        );
    });
  }
}
