export default function selectTool(toolName) {
  switch (toolName.toLowerCase()) {
    case "rectangle":
      cy.get("button[name=bx-tool-rectangle]").click("center");
      break;
    case "ellipse":
      cy.get("button[name=bx-tool-ellipse]").click("center");
      break;
    case "text":
      cy.get("button[name=bx-tool-text]").click("center");
      break;
    case "icon":
      cy.get("button[name=bx-tool-icon]").click("center");
      break;
    case "image":
      cy.get("button[name=bx-tool-image]").click("center");
      break;
    default:
      console.log("Invalid tool name");
      break;
  }
}
