export default function previewTests() {
  context("Opening in Preview Mode", () => {
    beforeEach(function() {
      Cypress.Cookies.preserveOnce("access-token");
    });

    it("Tools Should not be visible", function() {
      cy.wait(2000);
      cy.get("button[name=bx-tool-default]").should("not.exist");
      cy.get("button[name=bx-tool-rectangle]").should("not.exist");
      cy.get("button[name=bx-tool-ellipse]").should("not.exist");
      cy.get("button[name=bx-tool-image]").should("not.exist");
      cy.get("button[name=bx-tool-text]").should("not.exist");
      cy.get("button[name=bx-tool-icon]").should("not.exist");
      cy.get("button[name=bx-tool-pen]").should("not.exist");
    });

    it("Tutorial should not be visible", function() {
      cy.contains(/^This is an artboard. An artboard corresponds$/i).should(
        "not.exist"
      );
    });

    it("Code Editor Should be visible", function() {
      cy.get("#app-tutorial-code-editor").should("be.visible");
    });

    it("right panel editors should not be visible", function() {
      cy.get("#app-tutorial-right-panel .formGroupX").should("not.exist");
    });

    it("Add artboard Button should not be visible", function() {
      cy.get("[name=add-artboard]").should("not.exist");
    });

    it("wont show other options like download share in header bar", function() {
      cy.get("div.textX")
        .contains("Design")
        .should("not.exist");
      cy.get("div.textX")
        .contains("Code")
        .should("not.exist");
      cy.get("div.textX")
        .contains("Share")
        .should("not.exist");

      //Still To add For zoom downshift and download button
    });
  });
}
