    generateSourcePropCode(imageName: string, category?: string): string;
    generateJSLiteralCode: (literal: any, extraParams: ExtraParams, jsExpression?: boolean) => string;
    generateJSObjectCode: (obj: any, extraParams: ExtraParams) => string;
