var child_process = require("child_process");

var uploadFile = function(url, filePath, fileName, version, timestamp, token) {
  console.log("asda", filePath, fileName, version, timestamp, token);
  var cmd = `curl -X POST \
  -F "version=${version}" \
  -F "file_name=${fileName}"\
  -F "token=${token}"\
  -F "file=@${filePath}" \
  ${url}`;

  var child = child_process.exec(cmd);
  child.stdout.on("data", data => {
    console.log(`${data}`);
  });
  child.stderr.on("data", data => {
    console.error(`${data}`);
  });
};

module.exports = uploadFile;
