import BaseCodeGen from "../BaseCodeGen";

export interface Context {
  styles: {
    [key: string]: any;
  };
  imports: {
    [key: string]: {
      named: Array<string | object>;
      default: string | false;
    };
  };
  iconCount: number;
  filePath: string;
  conditionals: any;
  baseCodeGen: BaseCodeGen;
}

export type Config = {
  isPreviewFile: boolean;
  convertToFlex: {
    enclosing: boolean;
    stacking: boolean;
    proximity: boolean;
    smartFlex: boolean;
  };
};

export type ExtraParams = { parent: any; textLiteral?: any };
