import { ICommand } from "../lib";

export default class ChangeName implements ICommand {
  displayName = "ChangeName";
  store: any;
  newName: string;
  oldName: string;
  execCallback: () => void;
  constructor(store: any, newName: string, execCallback: () => void) {
    this.store = store;
    this.newName = newName;
    this.execCallback = execCallback;
  }

  execute() {
    this.oldName = this.store.name;
    this.store.name = this.newName;
    this.execCallback();
  }

  undo() {
    this.store.name = this.oldName;
    console.log("Undo ", this.store.name);
    this.execCallback();
  }

  redo() {
    this.store.name = this.newName;
    console.log("Redo ", this.store.name);

    this.execCallback();
  }
}
