import * as React from "react";

export default class Node extends React.PureComponent<any, any> {
  nodeRef: any;
  renderChildren() {
    return;
    this.props.renderWrapper(
      ["Children1", "Children2"].map((layer: any, i: number) => {
        if (layer.type === "unknown") {
          return null;
        }
        return [
          <Node
            key={"nodeData" + "_" + this.props.fetchPath(layer)}
            renderNode={this.props.renderNode}
            renderDivider={this.props.renderDivider}
            renderWrapper={this.props.renderWrapper}
            fetchChildren={this.props.fetchChildren}
            fetchPath={this.props.fetchPath}
          />
        ];
      })
    );
  }

  render(): any {
    return this.props.renderNode({}, this.renderChildren(), (ref: any) => {
      this.nodeRef = ref;
    });
  }
}
