import * as React from "react";
import {
  FormRow,
  FormCol,
  BorderRadius,
  TopLeftRadius,
  TopRightRadius,
  BottomLeftRadius,
  BottomRightRadius,
  BorderRadiusLock,
  Icon,
  Slider,
  InputGroupInput
} from "components-x";

export default class BorderRadiusBox extends React.Component<any, any> {
  render() {
    return (
      <FormRow>
        <FormCol first={true} uiSize={2}>
          <BorderRadius>
            <TopLeftRadius
              selected={
                this.props.disabled ? false : this.props.borderRadius.top
              }
            />
            <TopRightRadius
              selected={
                this.props.disabled ? false : this.props.borderRadius.right
              }
            />
            <BottomLeftRadius
              selected={
                this.props.disabled ? false : this.props.borderRadius.left
              }
            />
            <BottomRightRadius
              selected={
                this.props.disabled ? false : this.props.borderRadius.bottom
              }
            />
            <BorderRadiusLock>
              <Icon
                name={this.props.borderRadius.lock ? "lock" : "unlock"}
                size={16}
              />
            </BorderRadiusLock>
          </BorderRadius>
        </FormCol>
        <FormCol first uiSize={7}>
          <Slider
            disabled={this.props.disabled}
            min={0}
            max={100}
            value={this.props.currentValue ? this.props.currentValue : 0}
          />
        </FormCol>
        <FormCol uiSize={2}>
          <InputGroupInput
            isDisabled={this.props.disabled}
            type="number"
            value={this.props.currentValue ? this.props.currentValue : 0}
            validator={e => {
              let val = parseInt(e.target.value, 10);
              return !isNaN(val) && val <= 100 && val >= 0;
            }}
          />
        </FormCol>
      </FormRow>
    );
  }
}
