import * as React from "react";
import { CustomPicker, SketchPicker } from "react-color";

import { FormRow, FormCol, Button, Icon } from "components-x";

class GradientEditor extends React.Component<any, any> {
  render() {
    return (
      <div>
        <div
          style={{
            zIndex: 1
          }}
        >
          <FormRow noPadding horizontalPadding>
            <FormCol uiSize={1}>
              <Button
                disabled={false}
                borderTopRadius
                active={true}
                uiBackground={700}
                flexible={true}
                icon={<Icon name="icon-blank" size={16} />}
                // tooltip={<TooltipText>Solid</TooltipText>}
              />
            </FormCol>
          </FormRow>
          <SketchPicker
            width="160"
            color={this.props.color}
            disableAlpha={!this.props.enableAlpha}
          />
          <React.Fragment>
            <FormRow>
              <FormCol first uiSize={1}>
                <Button
                  transparent
                  icon={<Icon name={"icon-rotate-left"} size={12} />}
                  // tooltip={<TooltipText>Rotate Left</TooltipText>}
                />
              </FormCol>
              <FormCol first uiSize={1}>
                <Button
                  transparent
                  icon={<Icon name={"icon-rotate-right"} size={12} />}
                  // tooltip={<TooltipText>Rotate Right</TooltipText>}
                />
              </FormCol>
            </FormRow>
          </React.Fragment>
        </div>
      </div>
    );
  }
}

export default CustomPicker(GradientEditor);
