import * as React from "react";
import * as ReactDOM from "react-dom";
import App from "./App";
import ReactTooltip from "react-tooltip";

import { BrowserRouter } from "react-router-dom";
import { GlobalStyle } from "../lib/core";

ReactDOM.render(
  <BrowserRouter>
    <GlobalStyle />
    <App />
    <ReactTooltip
      effect="solid"
      place="bottom"
      multiline
      className="tooltipX"
    />
    <ReactTooltip
      effect="solid"
      place="top"
      id="tooltip-top"
      multiline
      className="tooltipX"
    />
    <ReactTooltip
      effect="solid"
      place="right"
      id="tooltip-right"
      multiline
      className="tooltipX"
    />
    <ReactTooltip
      effect="solid"
      place="bottom"
      id="tooltip-bottom"
      multiline
      className="tooltipX"
    />
    <ReactTooltip
      effect="solid"
      place="left"
      id="tooltip-left"
      multiline
      className="tooltipX"
    />
  </BrowserRouter>,
  document.getElementById("root") as HTMLElement
);
