import styled from "styled-components";
// import COLORS from "./constants/COLORS";
import Color from "./types/ColorPropType";
import createComponent from "../createComponent";
import * as classNames from "classnames";
import * as React from "react";

interface PropType extends Color {
  center?: boolean;
  right?: boolean;
}
export default createComponent(
  `
  .checkboxX {
    flex-grow: 1;
    display: flex;
    align-items: center;

}
.checkboxX.right{

}

  `,
  (props: PropType & React.HTMLProps<HTMLDivElement>) => {
    const { center, right, ...remainingProps } = props;

    const className = classNames({
      checkboxX: true,
      center: center ? true : false,
      right: right ? true : false
    });

    return (
      <div
        {...remainingProps}
        style={{
          ...remainingProps.style,
          justifyContent: center ? "center" : right ? "flex-end" : "flex-start"
        }}
        className={
          className +
          (remainingProps.className ? " " + remainingProps.className : "")
        }
      />
    );
  }
);
