import styled from "styled-components";
import Size from "./types/SizePropType";
import createComponent from "../createComponent";
import * as classNames from "classnames";
import * as React from "react";

interface PropType extends Size {
  contentRight?: boolean;
  contentCenter?: boolean;
}
export default createComponent(
  `
  .colX {
    display: flex;
    justify-content: flex-start;
    align-items: center;
  }
  .colX.contentRight{
    justify-content: flex-end;
  }
  .colX.contentCenter{
    justify-content: center;
  }
  `,
  (props: PropType & React.HTMLProps<HTMLDivElement>) => {
    const { uiSize, contentRight, contentCenter, ...remainingProps } = props;

    const className = classNames({
      colX: true,
      contentRight: contentRight ? true : false,
      contentCenter: contentCenter ? true : false
    });

    return (
      <div
        {...remainingProps}
        style={{
          ...remainingProps.style,
          flexGrow: uiSize ? (uiSize as any) : 1
        }}
        className={
          className +
          (remainingProps.className ? " " + remainingProps.className : "")
        }
      />
    );
  }
);
