import { spacer, sizes } from "../constants/SPACER";

const directions = { t: "top", l: "left", r: "right", b: "bottom" };
const axis = ["x", "y"];
const boxProps = { m: "margin", p: "padding" };

var spacerCSS = "";

// tslint:disable-next-line:forin
for (let i in sizes) {
  const spaceMagnitude = sizes[i] * spacer;

  // tslint:disable-next-line:forin
  for (var boxProp in boxProps) {
    // tslint:disable-next-line:forin
    for (let direction in directions) {
      spacerCSS += `.${boxProp}${direction}-${i} { ${boxProps[boxProp]}-${
        directions[direction]
      }: ${spaceMagnitude}px !important; }`;
    }

    spacerCSS += `.${boxProp}-${i} { ${
      boxProps[boxProp]
    }: ${spaceMagnitude}px !important; }`;

    spacerCSS += `.${boxProp}x-${i} {
${boxProps[boxProp]}-left: ${spaceMagnitude}px !important;
${boxProps[boxProp]}-right: ${spaceMagnitude}px !important;
    }`;

    spacerCSS += `.${boxProp}y-${i} {
${boxProps[boxProp]}-top: ${spaceMagnitude}px !important;
${boxProps[boxProp]}-bottom: ${spaceMagnitude}px !important;
    }`;
  }
}

export default spacerCSS;
