const Icon = React.forwardRef((props: any, ref: any) => {
  let passedProps = { ...props };
  delete passedProps["marginRight"];
  delete passedProps["name"];
  delete passedProps["size"];
  delete passedProps["inactive"];
  delete passedProps["active"];
  delete passedProps["uiColor"];
  delete passedProps["uiBackground"];
  delete passedProps["actionBtn"];
  delete passedProps["marginLeft"];
  delete passedProps["onPress"];
  return (
    <img
      {...passedProps}
      ref={ref}
      src={props.name ? iconMap[props.name] : iconMap["group"]}
      width={props.size ? props.size : 32}
      height={props.size ? props.size : 32}
      style={{
        border: "none",
        marginRight: props.marginRight ? 5 : 0,
        marginLeft: props.marginLeft ? 5 : 0,
        opacity: props.inactive ? 0.6 : 1,
        color: props.uiColor ? COLORS[props.uiColor] : undefined,
        background: props.uiBackground
          ? props.actionBtn
            ? secondary[props.uiBackground]
            : COLORS[props.uiBackground]
          : undefined,
        ...props.style
      }}
    />
  );
});
export default Icon;
