import styled from "styled-components";
import createComponent from "../createComponent";
import * as classNames from "classnames";
import * as React from "react";
interface PropType {
  height?: number;
  width?: number;
  marginRight?: number;
  marginLeft?: number;
}
export default createComponent(
  `
  .iconInJsWrapperX {


   }

  `,
  (props: PropType & React.HTMLProps<HTMLDivElement>) => {
    const { height, width, marginRight, marginLeft, ...remainingProps } = props;

    const className = classNames({
      iconInJsWrapperX: true
    });

    return (
      <div
        {...remainingProps}
        style={{
          ...remainingProps.style,
          height: height ? height + "px" : "16px",
          width: width ? width + "px" : "16px",
          marginRight: marginRight ? marginRight + "px" : "5px",
          marginLeft: marginLeft ? marginLeft + "px" : "5px"
        }}
        className={
          className +
          (remainingProps.className ? " " + remainingProps.className : "")
        }
      />
    );
  }
);

