import styled from "styled-components";
import createComponent from "../createComponent";
import * as classNames from "classnames";
import * as React from "react";

interface PropType {
  expand?: boolean;
  hidden?: boolean;
}
export default createComponent(
  `
  .iconListWrapperX {

    height: "auto",
    max-height:120px;
    overflow:hidden;
    visibility:auto;
   }
   .iconListWrapperX.expand{
       max-height:400px;
       overflow:scroll;

   }
   .iconListWrapperX.hidden{
     visibility:hidden;
  }
  `,
  (props: PropType & React.HTMLProps<HTMLDivElement>) => {
    const { height, width, expand, hidden, ...remainingProps } = props;

    const className = classNames({
      iconListWrapperX: true,
      expand: expand ? true : false,
      hidden: hidden ? true : false
    });

    return (
      <div
        {...remainingProps}
        style={{
          ...remainingProps.style
        }}
        className={
          className +
          (remainingProps.className ? " " + remainingProps.className : "")
        }
      />
    );
  }
);
