import styled from "styled-components";
import createComponent from "../createComponent";
import * as classNames from "classnames";
import * as React from "react";
// import COLORS from "./constants/COLORS";

interface PropType {
  paddingLeft?: boolean;
  paddingRight?: boolean;
  width?: string;
}

export default createComponent(
  `
    .inputGroupRightX {
      padding-left: 0;
      padding-right: 0;
    }
    .inputGroupRightX.paddingLeft{
      padding-left: 8px;
    }
    .inputGroupRightX.paddingRight{
      padding-right: 8px;
    }

`,
  (props: PropType & React.HTMLProps<HTMLDivElement>) => {
    const { paddingLeft, paddingRight, width, ...remainingProps } = props;

    const className = classNames({
      inputGroupRightX: true,
      paddingLeft: paddingLeft ? true : false,
      paddingRight: paddingRight ? true : false
    });

    return (
      <div
        {...remainingProps}
        style={{
          ...remainingProps.style,
          width: width ? width : "100%"
        }}
        className={
          className +
          (remainingProps.className ? " " + remainingProps.className : "")
        }
      />
    );
  }
);

