import * as React from "react";

export default class Thumbnail extends React.Component<any, any> {
  render() {
    // //console.log(this.props.name, iconMap[this.props.name], "iconName");
    return (
      <img
        src={this.props.source}
        width={this.props.size ? this.props.size * 1.5 : 72}
        height={this.props.size ? this.props.size : 48}
        style={{
          border: "none",
          objectFit: "cover",
          margin: this.props.margin ? 5 : 0
        }}
        {...this.props}
      />
    );
  }
}
