import * as React from "react";
import {
  DialogX,
  Alert,
  Prompt,
  Confirm,
  MinFileSize,
  ConfirmOnExit,
  SketchImportDialog,
  ProgressIndicatorDialog,
  ShareAccessDialog,
  ShareInviteDialog
} from "../lib";

class App extends React.Component {
  public render() {
    return (
      <div>
        <h1>Hello</h1>

        <button
          onClick={async () => {
            const x = await Alert("Ye Alert", "Aur ye uska Message");
            console.log(x);
          }}
        >
          show alert
        </button>
        <button
          onClick={async () => {
            const x = await Prompt("Ye prompt", "Input me kuchh likhna hai");
            console.log(x);
          }}
        >
          show prompt
        </button>

        <button
          onClick={async () => {
            const x = await Confirm(
              "Aur ye confirm",
              "Isme do button hote hain",
              "Do It",
              "Don't Do It"
            );
            console.log(x);
          }}
        >
          show confirm
        </button>
        <button
          onClick={async () => {
            const x = await MinFileSize(100);
            console.log(x);
          }}
        >
          show MinFileSize
        </button>
        <button
          onClick={async () => {
            const x = await ConfirmOnExit(
              "Unsaved files",
              "It looks like you have been editing something. If you leave before saving, your changes will be lost."
            );
            console.log(x);
          }}
        >
          Confirm on exit
        </button>
        <button
          onClick={async () => {
            const x = await SketchImportDialog(
              [
                { id: "1", name: "First" },
                { id: "2", name: "Second" },
                { id: "3", name: "Third" }
              ],
              []
            );
            console.log(x);
          }}
        >
          Sketch Import
        </button>
        <button
          onClick={async () => {
            await ProgressIndicatorDialog("", "Artboard 1 of 2", 50);
          }}
        >
          Progress Indicator
        </button>
        <DialogX />
      </div>
    );
  }
}

