import React from "react";
import { Display } from "../lib";

const layer = {
  name: "Sanket",
  children: [{ name: "Sahu" }, { name: "Second", children: [{ name: "Abc" }] }]
};
const layerCompiled = [
  {
    name: "Sanket",
    children: [
      {
        name: "Sahu",
        width: 100,
        height: 100,
        children: [{ name: "asdasd" }]
      },
      { name: "Second", children: [{ name: "Abc", ref: layer }] }
    ]
  }
];
class App extends React.Component {
  render() {
    return <Display layer={layer} layerCompiled={layerCompiled} />;
  }
}

export default App;
