import { IFormControl } from "common-interfaces";
import { Layer } from "domain-x";
export default class FormControl implements IFormControl {
  type: "string" | "color" | "number" | "callback" | "icon" | "navigation";
  title: string;
  layerPath: string;

  constructor(
    type: "string" | "color" | "number" | "callback" | "icon" | "navigation",
    title: string,
    layerPath: string
  ) {
    this.type = type;
    this.title = title;
    this.layerPath = layerPath;
  }
}
