import { Serializable, types } from "serializable";

export default class Import extends Serializable {
  layerName: string;
  packageName: string;
  isDefaultImport: boolean;
  asName: string;
  constructor(layerImport: Import) {
    super();
    if (layerImport) {
      this.layerName = layerImport.layerName;
      this.packageName = layerImport.packageName;
      this.isDefaultImport = layerImport.isDefaultImport;
      this.asName = layerImport.asName;
    }
  }
  getFields(): { [key: string]: any } {
    return {
      layerName: types.literal,
      packageName: types.literal,
      isDefaultImport: types.literal,
      asName: types.literal
    };
  }
}
