import Ui from "./Ui";
import Import from "./Import";
import Props from "./Props";
import Template from "./Template";
import ConditionalExpression from "./ConditionalExpression";
import Expression from "./Expression";
import VirtualProps from "../Collection/VirtualProps";

export default interface LayerNode<ParentType, ChildType> {
  type: string;
  props?: Props | ConditionalExpression | Expression | VirtualProps;

  // exclusive to Layer
  import?: Import;
  selected: boolean;
  hovered: boolean;
  collapsed: boolean;
  locked: boolean;
  dragHoverInside: boolean;
  dragHoverParent: boolean;
  dragHoverBefore: boolean;
  dragHoverAfter: boolean;
  ui?: Ui;
  template?: Template;
  styleName?: string;
  parent?: ParentType;

  // exclusive to File
  path?: string;
  content?: string;
  isDirty?: boolean;
  layer?: ChildType;
  ref?: any;
  isResizerVisible?: boolean;
  header?: any;
}
