import { types, Serializable } from "serializable";
import { Model } from "model";

export default class Layout extends Model {
  x: number;
  y: number;
  width: number;
  height: number;

  getFields(): { [key: string]: any } {
    return {
      x: types.literal,
      y: types.literal,
      width: types.literal,
      height: types.literal
    };
  }
}
