import { Model } from "model";
import { types } from "serializable";
import {
  IOverrideModel,
  overrideType,
  overrideCategory
} from "common-interfaces";

export default class OverrideModel extends Model implements IOverrideModel {
  overrideKey: string;
  type: overrideType;
  defaultValue: any;
  category: overrideCategory;
  propertyName: string;

  constructor(
    overrideKey: string,
    type: overrideType,
    defaultValue: any,
    category: overrideCategory,
    propertyName: string
  ) {
    super();
    this.overrideKey = overrideKey;
    this.type = type;
    this.defaultValue = defaultValue;
    this.category = category;
    this.propertyName = propertyName;
  }

  getFields(): { [key: string]: any } {
    return {
      overrideKey: types.literal,
      type: types.literal,
      defaultValue: types.literal,
      category: types.literal,
      propertyName: types.literal
    };
  }
}
