import { types } from "serializable";
import { Model } from "model";

export default class Template extends Model {
  centerVertical: boolean;
  centerHorizontal: boolean;
  repetitions: number;

  constructor(template: Template) {
    super();
    this.centerVertical = template.centerVertical;
    this.centerHorizontal = template.centerHorizontal;
    this.repetitions = template.repetitions;
  }
  getFields() {
    return {
      centerVertical: types.literal,
      centerHorizontal: types.literal,
      repetitions: types.literal
    };
  }
  isCenterVertical() {
    return this.centerVertical;
  }
  isCenterHorizontal() {
    return this.centerHorizontal;
  }
  setCenterVertical(centerVertical: boolean) {
    this.centerVertical = centerVertical;
  }
  setCenterHorizontal(centerHorizontal: boolean) {
    this.centerHorizontal = centerHorizontal;
  }
}
