import { find } from "lodash";
import {
  FlatList,
  Icon,
  ActivityIndicator,
  Switch,
  TabView,
  TabContent,
  Swiper,
  MapView,
  Picker,
  PickerItem,
  Slider
} from "./ThirdPartyComponents";

const thirdPartyComponentDefinitions = [
  FlatList,
  Icon,
  ActivityIndicator,
  Switch,
  TabView,
  TabContent,
  Swiper,
  MapView,
  Picker,
  PickerItem,
  Slider
];

export default function loadThirdPartyComponent(name: string) {
  return find(thirdPartyComponentDefinitions, def => {
    return def.name === name;
  });
}
