import { IEventCallable } from "./IEventCallable";
import SessionEvent from "./index";
export class EventCallableRegsitry implements IEventCallable {
  registry: Map<string, Array<(event: SessionEvent) => {}>>;

  constructor() {
    this.registry = new Map();
  }

  addEventListener(name: string, callback: any) {
    let callbacks = this.registry.get(name);
    if (callbacks) {
      callbacks.push(callback);
    } else {
      this.registry.set(name, [callback]);
    }
  }

  removeEventListener(name: string, callback: any) {
    let callbacks = this.registry.get(name);
    if (callbacks) {
      const indexOf = callbacks.indexOf(callback);
      if (indexOf > -1) {
        callbacks.splice(indexOf, 1);
      }
    }
  }

  dispatchEvent(name: string, event: SessionEvent) {
    // @ts-ignore
    if (window["debug"]) {
      console.log("DEBUG::", "GLUE Event:", name);
    }
    let callbacks = this.registry.get(name);
    if (callbacks) {
      for (let i in callbacks) {
        callbacks[i as any](event);
      }
    }
  }
}
