let logEnabled = false;

export const enableLog = () => {
  logEnabled = true;
};

export const rerendererLog = (...args: any[]) => {
  if (logEnabled) {
    return console.log(...args);
  }

  return () => {
    //
  };
};

export const rerendererTrace = (...args: any[]) => {
  if (logEnabled) {
    return console.trace(...args);
  }

  return () => {
    //
  };
};
