import { ISessionPlugin } from "common-interfaces";
import { Session } from "session";
import { apiSdk, ApiSdk } from "@builderx-core/api-sdk";

export default class ApiSdkPlugin implements ISessionPlugin {
  name = "ApiSdk";
  session: Session;
  apiSdk: ApiSdk;

  init(session: Session) {
    this.session = session;
    apiSdk.init({ localFontsUrl: "" });
    this.apiSdk = apiSdk;
  }

  activate() {}

  deactivate() {}
}
