import { Session } from "session";
import { SessionEvent } from "event-manager";

export default function addFilePatch(session: Session, filePath: string) {
  const event = new SessionEvent("addFile", {
    filePath
  });
  session.eventManager.dispatchEvent("addFile", event);
  session.domain.emit("files");

  const file = session.domain.files.get(filePath);
  // if (file && file.isArtboard()) {
  //   const event = new SessionEvent("focusArtboard", {
  //     domainFile: file
  //   });

  //   session.eventManager.dispatchEvent("focusArtboard", event);
  // }
}
