import { DomainLayer, DomainFile } from "domain-x";
import { Session, StageArtboard, Stage } from "session";
import { SessionEvent } from "event-manager";

export default function removeStagePatch(session: Session, stage: Stage) {
  session.eventManager.dispatchEvent(
    "removeStage",
    new SessionEvent("removeStage", {
      stage
    })
  );
}
