import { DomainLayer, DomainFile, Style } from "domain-x";
import { Session } from "session";
import { SessionEvent } from "event-manager";
import { forEach } from "lodash";

export default function setDrawTypePatch(session: Session, operation: any) {
  session.currentStage.tools.draw.setType(operation.value);
  session.currentStage.tools.draw.emit("type");
  session.currentStage.tools.emit("selectedTool");
  session.eventManager.dispatchEvent(
    "setDrawType",
    new SessionEvent("setDrawType", {
      drawType: operation.value
    })
  );
}
