import { DomainLayer, DomainFile } from "domain-x";
import { Session } from "session";
import { SessionEvent } from "event-manager";

export default function setHoveredLayersPatch(session: Session) {
  if (session.currentStage) {
    session.emit("hoveredLayers");

    const event = new SessionEvent("setHoverLayers", {
      layers: session.hoveredLayers
    });
    session.eventManager.dispatchEvent("setHoverLayers", event);
  }
}
