import { DomainLayer, DomainFile } from "domain-x";
import { Session } from "session";
import { SessionEvent } from "event-manager";

export default function setLayerAspectRatioPatch(
  session: Session,
  domainLayer: DomainLayer,
  operation: any
) {
  domainLayer.emit("aspectRatio");
  const event = new SessionEvent("setLayerAspectRatio", {
    layer: domainLayer,
    value: operation.value
  });
  session.eventManager.dispatchEvent("setLayerAspectRatio", event);
}
