import { DomainLayer, DomainFile } from "domain-x";
import { Session } from "session";
import { SessionEvent } from "event-manager";

export default function setLayerHiddenPatch(
  session: Session,
  domainLayer: DomainLayer,
  operation: any
) {
  const event = new SessionEvent("setLayerHide", {
    domainLayer: domainLayer,
    hide: operation.value
  });
  session.eventManager.dispatchEvent("setLayerHide", event);
}
