import { DomainLayer, DomainFile } from "domain-x";
import { Session } from "session";
import { SessionEvent } from "event-manager";

export default function setLayerLockedPatch(
  session: Session,
  domainLayer: DomainLayer,
  operation: any
) {
  domainLayer.emit("locked");
  const event = new SessionEvent("setLayerLock", {
    domainLayer: domainLayer,
    lock: operation.value
  });
  session.eventManager.dispatchEvent("setLayerLock", event);
}
