import { DomainLayer, DomainFile } from "domain-x";
import { Session } from "session";
import { SessionEvent } from "event-manager";

export default function setLayerResizerVisiblePatch(
  session: Session,
  layer: DomainLayer | DomainFile,
  operation: any
) {
  layer.emit("isResizerVisible");

  const event = new SessionEvent("setResizerVisibility", {
    layer,
    visibility: operation.value ? "show" : "hide"
  });
  session.eventManager.dispatchEvent("setResizerVisibility", event);
}
