import { DomainLayer, DomainFile } from "domain-x";
import { Session } from "session";
import { SessionEvent } from "event-manager";

export default function setProjectIsLocked(session: Session, lock: boolean) {
  session.emit("projectLock");

  if (lock) {
    session.eventManager.dispatchEvent(
      "lockProject",
      new SessionEvent("lockProject", {})
    );
  }
}
