import { DomainLayer, DomainFile } from "domain-x";
import { Session } from "session";
import { SessionEvent } from "event-manager";

export default function setSelectedFilePatch(session: Session) {
  session.emit("selectedFile");
  const event = new SessionEvent("selectFile", {
    file: session.selectedFile
  });

  session.eventManager.dispatchEvent("selectFile", event);
}
