import { DomainLayer, DomainFile } from "domain-x";
import { Session } from "session";
import { SessionEvent } from "event-manager";

export default function setSelectedLayersPatch(session: Session) {
  if (session.currentStage) {
    session.emit("selectedLayers");

    const event = new SessionEvent("selectLayers", {
      layers: session.getLayersFromSelectedLayerCollection()
      // generatePreview TODO: fix this
    });
    session.eventManager.dispatchEvent("selectLayers", event);
  }
}
