import { DomainLayer, DomainFile } from "domain-x";
import { Session } from "session";
import { SessionEvent } from "event-manager";

export default function setStatusBarPatch(session: Session, file: DomainFile) {
  file.emit("statusBar");
  const event = new SessionEvent("setStatusBar", {
    file
  });
  session.eventManager.dispatchEvent("setStatusBar", event);
}
