import { DomainLayer, DomainFile, Style } from "domain-x";
import { Session } from "session";
import { SessionEvent } from "event-manager";
import { forEach } from "lodash";

export default function setZoomPatch(session: Session, operation: any) {
  session.currentStage.tools.emit("zoom");
  const event = new SessionEvent("setZoom", {
    zoom: operation.value
  });
  session.eventManager.dispatchEvent("setZoom", event);
}
