import { DomainLayer, LogicalExpression } from "domain-x";
import { Layer, PathEvent } from "@builderx-core/designer";
import { forEach, find } from "lodash";
import { SessionEvent } from "event-manager";
import { Session, StageArtboard, Mutators } from "session";

function applySideEffects(session: Session, mode: string) {
  if (mode === "preview") {
    session.currentStage.canvas.executeFunction("switchToPreviewMode");
  }
  if (mode === "stage") {
    session.currentStage.canvas.executeFunction("switchToStageMode");
  }
}

export default function ChangeDisplayModeGlue(
  session: Session,
  e: SessionEvent
) {
  applySideEffects(session, e.payload.mode);
}
